import * as React from 'react';
import { 
  BottomNavigation, 
  Card, 
  Title, 
  Paragraph, 
  Text,
  TextInput,
} from 'react-native-paper';
import Onboard1 from './assets/onboard1.png'
import Create2 from './assets/create2.png'

const OnboardRoute = () => (
  <Card>

    <Card.Cover  source={Onboard1} style={{ width: '100%', height: 390 }}/>

    <Card.Content>
      <Title style={{ justifyContent: 'center', alignItems: 'center' }}>Learn About</Title>
      <Paragraph>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</Paragraph>
    </Card.Content>
  </Card>
);

const CreateRoute = () => (
  <Card>

    <Card.Cover  source={Create2} style={{ width: '100%', height: 390 }}/>

    <Card.Content>
      <Title style={{ justifyContent: 'center', alignItems: 'center' }}>Create Account</Title>
    </Card.Content>

    <Card>
      <TextInput
        label="Email"
        value={text}
        onChangeText={text => setText(text)}
      />
</Card>



  </Card>





);






const LoginRoute = () => <Text>Login</Text>;

const MyComponent = () => {
  const [index, setIndex] = React.useState(0);
  const [routes] = React.useState([
    { key: 'onboard', title: 'Onboard', icon: 'queue-onboard' },
    { key: 'create', title: 'Create', icon: 'create' },
    { key: 'login', title: 'Login', icon: 'login' },
  ]);

  const renderScene = BottomNavigation.SceneMap({
    onboard: OnboardRoute,
    create: CreateRoute,
    login: LoginRoute,
  });

  return (
    <BottomNavigation
      navigationState={{ index, routes }}
      onIndexChange={setIndex}
      renderScene={renderScene}
    />
  );
};

export default MyComponent;
