import * as React from 'react';
import { BottomNavigation, Card, Text } from 'react-native-paper';
import Onboard1 from './assets/onboard1.png'



// const OnboardRoute = () => {
//   return(
// <View>
//     <Card>
//       <Card.Cover  source={Onboard1}/>
//     </Card>
//   </View>
//   )
// };

const OnboardRoute = () => (
  <Card>
    <Card.Cover  source={Onboard1}style={{ flex:1, width: '100%'}}/>
  </Card>
);




const CreateRoute = () => <Text>Create</Text>;

const LoginRoute = () => <Text>Login</Text>;

const MyComponent = () => {
  const [index, setIndex] = React.useState(0);
  const [routes] = React.useState([
    { key: 'onboard', title: 'Onboard', icon: 'queue-onboard' },
    { key: 'create', title: 'Create', icon: 'create' },
    { key: 'login', title: 'Login', icon: 'login' },
  ]);

  const renderScene = BottomNavigation.SceneMap({
    onboard: OnboardRoute,
    create: CreateRoute,
    login: LoginRoute,
  });

  return (
    <BottomNavigation
      navigationState={{ index, routes }}
      onIndexChange={setIndex}
      renderScene={renderScene}
    />
  );
};

export default MyComponent;
