import * as React from 'react';
import { 
  Appbar, 
  Card,
  Title,
  Paragraph,
  Button,
  Avatar,
  Provider as PaperProvider,
  DefaultTheme,
  List,
  Divider,
 } from 'react-native-paper';
import { View } from 'react-native';
import WelcomeIamge from './assets/Welcome.png'


const HomeScreen=() => {

  return(
<View>
    <Card>

      <Card.Cover  source={WelcomeIamge} style={{ width: '100%', height: 700 }}/>
    </Card>
  </View>
  );
}


function SettingsScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <Card>

   <Card.Cover  source={WelcomeIamge} style={{ width: '100%', height: 700 }}/>
 </Card>
    </View>
  );
}


function ContactScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <Card>

   <Card.Cover  source={WelcomeIamge} style={{ width: '100%', height: 700 }}/>
 </Card>
    </View>
  );
}

// https://www.youtube.com/watch?v=NwX3mV3LeW8


const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={({route}) => ({
          headerTitle: () => <Text>Header</Text>,
          tabBarIcon: ({focused, color, size, padding}) => {
            let iconName;
            if(route.name === 'Home') {
                iconName = focused ? 'home' : 'home-outline'
            } else if (route.name === 'About') {
              iconName = focused ? 'information-circle' : 'information-circle-outline';
            } else if (route.name === 'Settings') {
              iconName = focused ? 'person' : 'person-outline';
            }

            return ( <Ionicons name={iconName} size={size} color={color} style={{paddingBottom: padding}} />
            );  
          }
        })}

        // tabBarOptions={{
        //   activeTintColor: 'lightseagreen',
        //   inactiveTintColour: 'grey',
        //   labelStyle: {fontSize: 16},
        //   style:{width: fullScreenWidth}

        // }}

      >
        <Tab.Screen name="Home" component={HomeScreen} />
        <Tab.Screen name="Settings" component={SettingsScreen} />
        <Tab.Screen name="Contact" component={ContactScreen} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}










